import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { useDispatch, useSelector } from "react-redux";
import setqty from '../../Redux/Cardprice/ItemQty';
import { useMemo } from 'react';
import { additem } from '../../Redux/Cardprice/PriceAddAction';
import { deletitem } from '../../Redux/Cardprice/PriceDeleteAction';

const DropDown=(props)=>{
    let numOfItem=useSelector(state=>state.price.numOfItem)
    let ItemPrice=useSelector(state=>state.price.totalPrice)
    let quantity=useSelector(state=>state.price.qty)

let dispatch=useDispatch()


   
    
      const [datakey,setdatakey]=React.useState([])
      const [num,setNum]=React.useState(1)
      const [qty, setQty] = React.useState('');

      React.useEffect(
        ()=>{
            setdatakey(Object.keys(props.list[0]))
            console.log(props.list[0],"aaaa")

            setQty(datakey[0])
            
        },[]
      )

    //   useMemo(()=>{
    //     if(num>numOfItem){
    //         dispatch(additem(parseInt(props.list[0][qty]*num)))
    //     }else if(num<numOfItem && num>0){
    //         dispatch(deletitem(parseInt(props.list[0][qty]*num)))

    //     }else{
    //      alert("qyantity less than zero")
    //     }
    //   },[qty||num])

      const handleChange = (event) => {

        setQty(event.target.value);
        
        dispatch(setqty(parseInt(props.list[0][event.target.value]*num),event.target.value,props.name,num))
        
      };
    return(
        <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
        <div style={{display:'flex',width:'120px',alignItems:'center'}}>    
            <InputLabel id="demo-simple-select-standard-label">type</InputLabel>
<Select sx={{width:'200px'}}
          labelId="demo-simple-select-standard-label"
          id="demo-simple-select-standard"
          value={qty}
          onChange={handleChange}
          label="Age"
        >
         {datakey?.map((e,i)=>{
            return(<MenuItem key={i} value={e}>{e}</MenuItem>)
         })}
         
        </Select> <span style={{paddingLeft:'23px'}}>{props.list[0][qty]}</span></div>
      </FormControl>
    )
}
export default DropDown
