const initialState={

name:{
    numOfItem:0,
    totalPrice:0,
    qty:'',
    name:''
}

}
 const PriceReducer=(State=initialState,action)=>{
    switch (action.type) {
        case 'DELETE':
            return{ 
                ...State,numOfCake:State.numOfItem-1,
                totalPrice:action.amount,
                
            }
       
        case 'ADD':return{
            ...State,numOfCake:State.numOfItem+1,
            totalPrice:action.amount,
           
        }
        case 'QTY':()=>{
            let names=action.name
           
            return Object.assign(State,{
                names:{
                    numOfItem:action.number,
                    totalPrice:action.amount,
                    qty:action.qty,
                    name:names
            
                }
                        })
        }
        
        default:return State
         
    }
}
export default PriceReducer
