import { Box, Divider, Grid, Stack, Typography } from "@mui/material"
import { Button, Input } from "@mui/joy"
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import Draggable from 'react-draggable';
import CloseRoundedIcon from '@mui/icons-material/CloseRounded';
import { useEffect, useState } from "react";
import Skeleton from '@mui/material/Skeleton';
import DropDown from "../../Components/Dropdown/DropDown";
import * as React from 'react';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import CardOverflow from '@mui/joy/CardOverflow';
import Chip from '@mui/joy/Chip';
import Link from '@mui/joy/Link';
import ArrowOutwardIcon from '@mui/icons-material/ArrowOutward';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import axios from "axios"
import AspectRatio from '@mui/joy/AspectRatio';
import Avatar from '@mui/joy/Avatar';
import IconButton from '@mui/joy/IconButton';
import FavoriteBorderRoundedIcon from '@mui/icons-material/FavoriteBorderRounded';
const OurMenu = () => {
  let [search, setsearch] = useState('')
  let [debouncing, setdebouncing] = useState('')
  const [searchload, setsearchload] = useState(true)
  const [alldata, setAlldata] = useState()
  const [load, setload] = useState(true)
  const [loadingfood, setLoadingFood] = useState(true)
  const [fooitems, setFoodItems] = useState([])
  const [carddetail, setcarddetail] = useState(false)
  const [imgid, setimgid] = useState({ name: "", img: '', details: '', category: '' });

  const fetchData = () => {
    axios.post("http://localhost:8000/bachulars/fooddata", {}, {
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => {
        setAlldata(response.data);
        setFoodItems(response.data?.fooditems);
        setLoadingFood(false);
        // Set loading to false after data is fetched
      })
      .catch(error => {
        console.error("Error fetching data:", error);
        setLoadingFood(false); // Reset loading state to true in case of error
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    // 
    if (loadingfood === false) {

      setload(true)
      console.log(loadingfood, "as");
    }
  }, [loadingfood])

  //debouncing logic
  useEffect(() => {

    let timer = setTimeout(() => {
      setdebouncing(search)
      setsearchload(true)
      console.log(search + "debounce");
    }, 900)

    return () => { clearTimeout(timer) }
  }, [search])


  const sarchfunction = (e) => {
    setsearchload(false)
    setsearch(e.target.value)

  }


  return (
    <>
      <div className="hide_scroolbar" style={{ width: '100%', height: '99vh', display: 'flex', justifyContent: 'center' }}>

        <Box sx={{ flexGrow: 1, paddingTop: '100px', display: 'flex', flexWrap: 'wrap', paddingBottom: '100px', maxWidth: '1400px' }}>
          <Grid container width='100%' className="lefttitel_main" textAlign={'center'} style={{ flexDirection: 'column' }}>
            <Typography variant="h2" style={{ fontWeight: '600' }}>Our best Seller DishesðŸ”¥ðŸ”¥</Typography>
            <Typography variant="h6" style={{ fontWeight: '400' }} width={'50%'}>Our fresh garden salad is a light and refreshing option. It features a mix of crisp lettuce, juicy tomatoe all tossed in your choice of dressing.</Typography>

          </Grid>

          <Draggable><Grid container width='100%' className="search_btn_position"  > <Input onChange={sarchfunction} value={search}
            startDecorator={<SearchOutlinedIcon />}
            endDecorator={<Button style={{ background: 'red' }} onClick={() => { setsearch("") }}><CloseRoundedIcon fontSize="large" /></Button>}
            sx={{
              'width': '430px',
              "--Input-radius": "30px",
              "--Input-gap": "30px",
              "--Input-placeholderOpacity": 0.5,
              "--Input-focusedThickness": "1px",
              "--Input-minHeight": "50px",
              "--Input-paddingInline": "15px",
              "--Input-decoratorChildHeight": "33px"
            }}
          ></Input></Grid></Draggable>






          <Box sx={{ overflow: 'hidden', width: '100%' }}>
            <>
              {['Starter', 'Biryani/Rice', 'Pizza', 'Juice'].map((e, i) => {
                return (<>
                  <div key={i} style={{ width: '100%', padding: '0px 50px', paddingTop: '100px', display: 'flex', alignItems: 'center', justifyContent: 'flex-start' }} > <h3>{e}</h3></div>
                  <Grid container wrap="wrap" padding='0px 50px' marginTop={'30px'} display={'flex'} justifyContent={'flex-start'}>
                    {fooitems.filter((itm, i) => {

                      return ((itm?.CategoryName === e) && (itm?.name?.toLowerCase().includes(debouncing.toLowerCase())))
                    }).map((item, index) => (

                      <>
                        {loadingfood ? console.log("true") : console.log("false")}
                        <Card key={index} sx={{ width: 240, maxWidth: '100%', boxShadow: 'lg', margin: '20px', padding: '8px', paddingBottom: '20px' }}>
                          <CardOverflow>          {load && searchload ? (
                            <img
                              style={{ width: '100%', height: 150, margin: '8px 0px', borderRadius: '8px' }}
                              alt={item.title}
                              src={item.img}
                              loading="lazy"
                            />
                          ) : (
                            <Skeleton variant="rectangular" width={'100%'} height={150} />

                          )}

                            {load && searchload ? (
                              <Box sx={{ pr: 2 }}>
                                <CardContent>
                                  <Typography level="body-xs">{item.name}</Typography>


                                  <div onClick={(e) => {
                                    setimgid({ ...imgid, name: item?.name, img: item?.img, details: item?.description, category: item?.CategoryName })
                                    setimgid(e.target.id)
                                    e.stopPropagation();
                                    setcarddetail(!carddetail)
                                  }} id={item.name} ><Link href="#underline" underline="hover">
                                      details <ArrowOutwardIcon />
                                    </Link></div>



                                  <Typography
                                    level="title-lg"
                                    sx={{ mt: 1, fontWeight: 'xl' }}
                                    endDecorator={
                                      <Chip component="span" size="sm" variant="soft" color="success">
                                        Lowest price
                                      </Chip>
                                    }
                                  >
                                    2,900 THB
                                  </Typography>
                                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', paddingBottom: '10px 0px' }}>
                                    <DropDown list={item.options}></DropDown> <ShoppingCartIcon></ShoppingCartIcon>
                                  </div>                </CardContent>
                              </Box>
                            ) : (
                              <Box sx={{ pt: 0.5 }}>
                                <Skeleton width={'50%'} />
                                <Skeleton width="100%" />
                                <Skeleton width={'60%'} />
                                <Skeleton width="100%" />
                              </Box>
                            )}



                          </CardOverflow>
                        </Card>




                        {(carddetail ) && <div id="cardouter" style={{ backgroundColor: '#0606063b', height: '100vh', width: '100%', position: 'fixed', top: '0px', display: 'flex', alignItems: 'center', justifyContent: 'center' }} onClick={(e) => {
                          if (e.target.id == 'cardouter') {
                            setcarddetail(!carddetail)
                          }
                        }} >
                          <div id="inner" style={{
                            backgroundColor: 'red', padding: '22px'
                          }}>

                            <Card
                              variant="outlined"
                              sx={(theme) => ({
                                width: 300,
                                gridColumn: 'span 2',
                                flexDirection: 'row',
                                flexWrap: 'wrap',
                                resize: 'horizontal',
                                overflow: 'hidden',
                                gap: 'clamp(0px, (100% - 360px + 32px) * 999, 16px)',
                                transition: 'transform 0.3s, border 0.3s',
                                '&:hover': {
                                  borderColor: theme.vars.palette.primary.outlinedHoverBorder,
                                  transform: 'translateY(-2px)',
                                },
                                '& > *': { minWidth: 'clamp(0px, (360px - 100%) * 999,100%)' },
                              })}
                            >
                              <AspectRatio
                                variant="soft"
                                sx={{
                                  flexGrow: 1,
                                  display: 'contents',
                                  '--AspectRatio-paddingBottom':
                                    'clamp(0px, (100% - 360px) * 999, min(calc(100% / (16 / 9)), 300px))',
                                }}
                              >
                                <img
                                  src={imgid.img}
                                  loading="lazy"
                                  alt={imgid.name}
                                />
                              </AspectRatio>
                              <Box
                                sx={{
                                  display: 'flex',
                                  flexDirection: 'column',
                                  gap: 2,
                                  maxWidth: 200,
                                }}
                              >
                                <Box sx={{ display: 'flex' }}>
                                  <div>
                                    <Typography level="title-lg">
                                      <Link
                                        href="#container-responsive"
                                        overlay
                                        underline="none"
                                        sx={{
                                          color: 'text.primary',
                                          '&.Mui-focusVisible:after': { outlineOffset: '-4px' },
                                        }}
                                      >
                                        Yosemite National Park
                                      </Link>
                                    </Typography>
                                    <Typography level="body-sm">{img.name}</Typography>
                                  </div>
                                  <IconButton
                                    size="sm"
                                    variant="plain"
                                    color="neutral"
                                    sx={{ ml: 'auto', alignSelf: 'flex-start' }}
                                  >
                                    <FavoriteBorderRoundedIcon color="danger" />
                                  </IconButton>
                                </Box>
                                <AspectRatio
                                  variant="soft"
                                  sx={{
                                    '--AspectRatio-paddingBottom':
                                      'clamp(0px, (100% - 200px) * 999, 200px)',
                                    pointerEvents: 'none',
                                  }}
                                >
                                  <img
                                    alt={imgid.name}
                                    src={imgid.img}
                                  />
                                </AspectRatio>
                                <Box sx={{ display: 'flex', gap: 1.5, mt: 'auto' }}>
                                  <Avatar variant="soft" color="neutral">
                                    Y
                                  </Avatar>
                                  <div>
                                    <Typography level="body-xs">Designed by</Typography>
                                    <Typography level="body-sm">Nature itself</Typography>
                                  </div>
                                </Box>
                              </Box>
                            </Card>


                          </div>

                        </div>}</>
                    ))}
                  </Grid>
                </>)
              })}
            </>
          </Box>
        </Box>


      </div>
    </>
  )

}



export default OurMenu
